import React, { Component } from 'react'
import logo from './logo_carrefo.png'

export class AccountSetup extends Component {
    continue = e => {
        e.preventDefault();
        this.props.nextStep();
    };

    render() {
        const { values, inputChange } = this.props;

        return (
            <div className="form-container">
            <img style={{margin:30}} src={logo} width="100"/>
                <h6 className="mb-7"><b>Bem-vindo</b> ao aplicativo do <b>Cart√£o Carrefour Drogaria</b></h6>
                <div className="form-group">
                    <input type="text" placeholder="Digite seu CPF" className="form-control" name="name" onChange={inputChange('name')} value={values.name} />
                </div>
                <div className="form-group">
                    <input placeholder="Email" type="email" className="form-control" name="email" onChange={inputChange('email')} value={values.email} />
                   <span class="focus-border"></span>
                </div>
                <div className="form-group">
                    <input placeholder="Celular"  type="number" className="form-control" name="phone" onChange={inputChange('phone')} value={values.phone} />
                </div>
                <div className="form-group">
                    <input placeholder="Senha"   type="password" className="form-control" name="password" onChange={inputChange('password')} value={values.password} />
                </div>

                <br />

                <div className="text-right">
                    <button className="btn btn-primary" onClick={this.continue}>Continue</button>
                </div>
            </div>
        )
    }
}

export default AccountSetup
